See I'm trying to create a handshake with asana But getting error that failed to do handshake , Here Is my code import {
  createTrigger,
  TriggerStrategy,
  type OAuth2PropertyValue,
  WebhookHandshakeStrategy,
} from '@activepieces/pieces-framework';

import { asanaCommon, callAsanaApi, callWebhookApi } from '../common';
import { HttpMethod } from '@activepieces/pieces-common';
import { asanaAuth } from '../../index';

export const projectTaskActivity = createTrigger({
  name: 'project_task_activity',
  displayName: 'Any Task Activity in Project',
  description: 'Triggers when any task is added, updated, removed, or deleted in a selected project.',
  auth: asanaAuth,
  type: TriggerStrategy.WEBHOOK,



  props: {
    workspace: asanaCommon.workspace,
    project: asanaCommon.project,
  },

  sampleData: {
    events: [
      {
        user: { gid: '12345', resource_type: 'user', name: 'John Doe' },
        created_at: '2023-05-15T15:00:00.000Z',
        action: 'added',
        resource: { gid: '67890', resource_type: 'task', name: 'Complete project documentation' },
        parent: { gid: '54321', resource_type: 'project', name: 'Marketing Campaign' },
      },
    ],
  },



  async onEnable({ propsValue, auth, webhookUrl, store }) {
    const projectId = propsValue.project;
    const accessToken = (auth as OAuth2PropertyValue).access_token;

    console.log('[Enable] Creating webhook for project:', projectId, 'Webhook URL:', webhookUrl);

    try {
      const response = await callWebhookApi(
        HttpMethod.POST,
        'webhooks',
        accessToken,
        {
          resource: '1210132224773890',
          target:webhookUrl,
          filters: [
            {
              resource_type: 'task',
              action: 'added',
            },
            {
              resource_type: 'task',
              action: 'changed'
            }
          ]
        }
      );
      const webhookId = response.body?.data?.gid;
      if (!webhookId) {
        throw new Error('[Enable] Asana webhook creation succeeded but no webhook ID was returned.');
      }

      await store.put('asana_webhook_gid', webhookId);
      console.log(`[Enable] Asana webhook created successfully: ${webhookId}`);
    } catch (err) {
      console.error('[Enable] Failed to create Asana webhook:', err);
      // throw err;
    }
  },

  async onDisable({ auth, store }) {
    const accessToken = (auth as OAuth2PropertyValue).access_token;
    const webhookId = await store.get('asana_webhook_gid');

    // if (!webhookId) {
    //   console.warn('[Disable] No stored webhook ID found.');
    //   return;
    // }

    // try {
    //   await callAsanaApi(HttpMethod.DELETE, `webhooks/${webhookId}`, accessToken, undefined);
    //   console.log(`[Disable] Deleted Asana webhook: ${webhookId}`);
    // } catch (err) {
    //   console.error(`[Disable] Failed to delete Asana webhook (${webhookId}):`, err);
    // }
  },

  async run({ payload }) {
    console.log('[Run] Received headers:', payload.headers);
    console.log('[Run] Received body:', payload.body);

    const secret = payload.headers?.['x-hook-secret'];
    console.log(secret,"secret for asana is here")
    if (secret) {
      return [{ 'x-hook-secret': secret }];
    }

    return [payload.body];
  },
  handshakeConfiguration: {
    strategy: WebhookHandshakeStrategy.HEADER_PRESENT,
    paramName: 'x-hook-secret',
  },
  async onHandshake(context) {
    const secret = context.payload.headers?.['x-hook-secret'];

    if (!secret) {
      console.error('[Handshake Error] Missing x-hook-secret header:', context.payload.headers);
      return {
        status: 400,
        body: 'Missing x-hook-secret',
      };
    }

    console.log('[Handshake] Responding with x-hook-secret:', secret);

    return {
      status: 200,
      headers: {
        'X-Hook-Secret': context.payload.headers?.['x-hook-secret'] // Fixed header name and value
      },
      // body: {} // Add empty body
    };
  },
});

